import React, { useState, useEffect } from "react";
import controleLivros from './controle/ControleLivros';
import ControleEditoras from './controle/ControleEditora';

const ControleLivro = new controleLivros();
const ControleEditora = new ControleEditoras();

const LinhaLivro = props => {
    const { livro, excluir } = props;
    return (
        <>
            <tr>
                <td>{livro.titulo}</td>
                <td>{livro.resumo}</td>
                <td>{ControleEditora.getNomeEditora(livro.codEditora)}</td>
                <td><ul>
                    {livro.autores.map(autor => (
                        <li>{autor}</li>
                    ))}
                </ul></td>
            </tr>
        </>
    );
};

class LivroLista extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            livro: {"codigo": 1,"codEditora": 1,"titulo": "titulo1","resumo": "xxxxx","autores": ['nome11','nome12','nome13']},
            excluir: "",
            nomeEditora: ControleEditora.getNomeEditora()
        };
    };
    render() {
        return(
            <tr>
                <td>{this.state.livro.titulo}</td>
                <td>{this.state.livro.resumo}</td>
                <td>Editora</td>
                <td><ul>
                    {this.state.livro.autores.map(autor => (
                        <li>{autor}</li>
                    ))}
                </ul></td>
            </tr>
        )
    };
};

export default LivroLista
