import { Livro } from '../modelo/Livro';

const baseURL = "http://localhost:3030/livros";

interface LivroMongo {
    _id: string | null;
    codEditora: number;
    titulo: string;
    resumo: string;
    autores: string[];
}

class ControleLivros {
    async obterLivros(){
        fetch(baseURL)
        .then((res) => res.json())
        .then((livros) => {
            let lista: any[] = [];
            livros.map((livro_:any) => {
                let livro: Livro = {
                    codigo: livro_._id,
                    codEditora: livro_.codEditora,
                    titulo: livro_.titulo,
                    resumo: livro_.resumo,
                    autores: livro_.autores,
                };
                lista.push(livro)
            });
            console.log(lista)
        });
    }

    async incluir(livro: Livro){
        let livro_m: LivroMongo = {
            _id: null,
            codEditora: livro.codEditora,
            titulo: livro.titulo,
            resumo: livro.resumo,
            autores: livro.autores,
        };
        fetch(baseURL, {
            method: "POST",
            body: JSON.stringify(livro_m),
            headers: { 
                "Content-Type": "application/json"
             }
        })
        .then( res => res.ok );
        console.log(JSON.stringify(livro_m))
    }

    async excluir(codigo:string){
        fetch(baseURL+codigo,{method:'DELETE'})
            .then( res => res.ok );
    }
}

export default ControleLivros
