import React, { useState, useEffect } from "react";
import controleLivros from './controle/ControleLivros';
import ControleEditoras from './controle/ControleEditora';

const ControleLivro = new controleLivros();
const ControleEditora = new ControleEditoras();

class LinhaLivro extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            livro: ControleLivro.obterLivros()[1],
            excluir: ControleLivro.excluir(),
            nomeEditora: ControleEditora.getNomeEditora(livro)
        };
    }
    render() {
        return(
            <table>
                <tr>
                    <th>Titulo</th>
                    <th>Resumo</th>
                    <th>Editora</th>
                    <th>Autores</th>
                </tr>
                    <tr key={livro.codigo}>
                        <td>{livro.titulo}</td>
                        <td>{livro.resumo}</td>
                        <td>Editora</td>
                        <td><ul>
                            {livro.autores.map(autor => (
                                <li key={index}>{autor}</li>
                            ))}
                        </ul></td>
                    </tr>
            </table>
        )
    }
}

export default LinhaLivro
