import React from 'react';
import controleLivros from './controle/ControleLivros';
import ControleEditoras from './controle/ControleEditora';

let ControleLivro = new controleLivros();
let ControleEditora = new ControleEditoras();

class LinhaLivro extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            livro: ControleLivro.obterLivros()[1],
            excluir: ControleLivro.excluir(),
            nomeEditora: ControleEditora.getNomeEditora(1)
        };
    }
    render() {
        return(
            <table>
                <tr>
                    <th>Titulo</th>
                    <th>Resumo</th>
                    <th>Editora</th>
                    <th>Autores</th>
                </tr>
                <tr>
                    <td>Titulo</td>
                    <td>Resumo</td>
                    <td>Editora</td>
                    <td>Autores</td>
                </tr>
            </table>
        )
    }
}

export default LinhaLivro
