import { useState, useEffec } from "react";
import { useNavigate } from 'react-router-dom';
import ControleLivros from './controle/ControleLivros';
import ControleEditoras from './controle/ControleEditora';
import { Livro } from "./modelo/Livro";

const controleLivro = new ControleLivros();
const controleEditora = new ControleEditoras();

export default function LivroDados() {
    const opcoes = controleEditora.getEditoras().map(function(editora) {
        const vetor = {};
        vetor['value'] = editora.codEditora;
        vetor['text'] = editora.nome;
        return vetor;
    });

    const [titulo, setTitulo] = useState("");
    const [resumo, setResumo] = useState("");
    const [autores, setAutores] = useState("");
    const [codEditora, setCodEditora] = useState(opcoes[0].value);
    const navigate = useNavigate();

    const tratarCombo = (e) => setCodEditora(Number(e.target.value));
    const incluir = (e) => {
        e.preventDefault();
        var livro = {
            codigo: 0,
            codEditora: codEditora,
            titulo: titulo,
            resumo: resumo,
            autores: autores.split("\n")
        }
        controleLivro.incluir(livro)
        navigate("/")
    }

    return(
        <main>
            <h1>Dados do Livro</h1>
            <form onSubmit={incluir}>
                <label typeof="text">Título</label><br/>
                <input onChange={(e) => setTitulo(e.target.value)} required></input><br/>
                <label>Resumo</label><br/>
                <textarea onChange={(e) => setResumo(e.target.value)} name="resumo"></textarea><br/>
                <label>Editora</label><br/>
                <select onChange={tratarCombo}>
                    {opcoes.map((opcao, index) => (
                        <option value={opcao.value} key={index}>{opcao.text}</option>
                    ))}
                </select><br/>
                <label>Autores (1 por linha)</label><br/>
                <textarea onChange={(e) => setAutores(e.target.value)}></textarea><br/>
                <button type="submit">Salvar Dados</button>
            </form>
        </main>
    );
};
