import React, { useState, useEffect } from "react";
import controleLivros from './controle/ControleLivros';
import ControleEditoras from './controle/ControleEditora';

const Llivros = [
    {
        "codigo": 1,
        "codEditora": 1,
        "titulo": "titulo1",
        "resumo": "xxxxx",
        "autores": ['nome11','nome12','nome13']
    },{
        "codigo": 2,
        "codEditora": 2,
        "titulo": "titulo2",
        "resumo": "xxxxx",
        "autores": ['nome21','nome22','nome23']
    },{
        "codigo": 3,
        "codEditora": 3,
        "titulo": "titulo3",
        "resumo": "xxxxx",
        "autores": ['nome31','nome32','nome33']
    },
]

class LinhaLivro extends React.Component {
    constructor(props) {
        super(props);
        const ControleLivro = new controleLivros();
        const ControleEditora = new ControleEditoras();
        this.state = {
            livro: ControleLivro.obterLivros(),
            excluir: ControleLivro.excluir(),
            nomeEditora: ControleEditora.getNomeEditora(1)
        };
    }
    render() {
        return(
            <table>
                <tr>
                    <th>Titulo</th>
                    <th>Resumo</th>
                    <th>Editora</th>
                    <th>Autores</th>
                </tr>
                    {this.state.livro.map(livro => (
                        <tr key={livro.codigo}>
                            <td>{livro.titulo}</td>
                            <td>{livro.resumo}</td>
                            <td>Editora</td>
                            <td>Autores</td>
                        </tr>
                    ))}
            </table>
        )
    }
}

export default LinhaLivro
