import { Editora } from '../modelo/Editora';

const editoras: Array<Editora> = [
    {
        "codEditora": 1,
        "nome": "Nova Fronteira",
    },{
        "codEditora": 2,
        "nome": "Gutenberg",
    },{
        "codEditora": 3,
        "nome": "Valentina",
    },
]

class ControleEditoras {
    constructor(){}

    getNomeEditora(codEditora:number){
        let editora: Editora[] = this.getEditoras().filter((editora) => editora.codEditora == codEditora )
        
        return editora
    }

    getEditoras(){
        return editoras
    }
}

let controle = new ControleEditoras()
console.log(controle.getNomeEditora(2))

export default ControleEditoras
