import React, { useState, useEffect } from "react";
import controleLivros from './controle/ControleLivros';
import ControleEditoras from './controle/ControleEditora';

const ControleLivro = new controleLivros();
const ControleEditora = new ControleEditoras();

const LinhaLivro = props => {
    const { livro, excluir } = props;
    return (
        <>
            <tr>
                <td>{livro.titulo}</td>
                <td>{livro.resumo}</td>
                <td>{ControleEditora.getNomeEditora(livro.codEditora)}</td>
                <td><ul>
                    {livro.autores.map(autor => (
                        <li>{autor}</li>
                    ))}
                </ul></td>
            </tr>
        </>
    );
};

export default function LivroLista() {
    const [livros, setLivros] = useState([]);
    const [carregado, setCarregado] = useState(false);
    
    useEffect(() => {
        setLivros(ControleLivro.obterLivros());
        setCarregado(true)
    }, []);

    return(
        <main>
            <table>
                <tr>
                    <td>Titulo</td>
                    <td>Resumo</td>
                    <td>Editora</td>
                    <td>Autores</td>
                </tr>
                {livros.map( livro => (
                    <LinhaLivro livro={livro} excluir={livro.codigo}/>
                ))}
            </table>
        </main>
    );
};
