import React, { useState, useEffect } from 'react';
import controleLivros from './controle/ControleLivros';
import ControleEditoras from './controle/ControleEditora';

let controleLivro = new controleLivros();
let ControleEditora = new ControleEditoras();

console.log(ControleEditora.getNomeEditora(3))

class LinhaLivro extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            livro: controleLivro.obterLivros(),
            excluir: "Albuquerque",
            nomeEditora: ControleEditora.getNomeEditora()
        };
    }
    render() {
        return(
            <tr>
                <th>Título</th>
                <th>Resumo</th>
                <th>Editora</th>
                <th>Autores</th>
            </tr>
        )
    }
}

export default LinhaLivro
