import Editora from '../modelo/Editora';

const editoras: Array<Editora> = [
    {
        "codEditora": 1,
        "nome": "Nova Fronteira",
    },{
        "codEditora": 2,
        "nome": "Gutenberg",
    },{
        "codEditora": 3,
        "nome": "Valentina",
    },
]

class ControleEditoras {
    constructor(){}

    getNomeEditora(codEditora:number){
        return this.getEditoras().filter(function(product){
            if(product.codEditora == codEditora){
                return product.nome
            }
        })
    }

    getEditoras(){
        return editoras
    }
}

console.log(ControleEditoras)

export default ControleEditoras
