import React, { useState, useEffect } from "react";
import controleLivros from './controle/ControleLivros';
import ControleEditoras from './controle/ControleEditora';

const ControleLivro = new controleLivros();
const ControleEditora = new ControleEditoras();

class LinhaLivro extends React.Component {
    constructor(props) {
        const Olivro = ControleLivro.obterLivros()[1]
        super(props);
        this.state = {
            livro: ControleLivro.obterLivros(),
            excluir: "",
            nomeEditora: ControleEditora.getNomeEditora(this.livro)
        };
    }
    render() {
        return(
            <tr>
                <td>{this.livro.resumo}</td>
                <td>{this.livro.resumo}</td>
                <td>Editora</td>
                <td><ul>
                    {this.livro.autores.map(autor => (
                        <li>{autor}</li>
                    ))}
                </ul></td>
            </tr>
        )
    }
}

export default LinhaLivro
